opts.click_handler:
function (e) {
					e.preventDefault();
					e.stopImmediatePropagation();
					if (toggle_status_open){
							jQuery('h2.hsection').each(function(){ jQuery(this).data('arc_section').hide(); });
							jQuery('h3.hsection').each(function(){ jQuery(this).data('arc_section').hide(); });
							jQuery('h4.hsection').each(function(){ jQuery(this).data('arc_section').hide(); });
							jQuery('#sect_status').each(function(){ jQuery(this).data('arc_section').show(); });
						//jQuery('h4.hsection').each(function(){ jQuery(this).data('arc_section').hide(); });
						//jQuery('#sect_p_collection').each(function(){ jQuery(this).data('arc_section').hide(); });
						//jQuery('#sect_p_info').each(function(){ jQuery(this).data('arc_section').hide(); });
						//jQuery('#sect_info').each(function(){ jQuery(this).data('arc_section').hide(); });
						toggle_status_open = false;
					} else {
							jQuery('h2.hsection').each(function(){ jQuery(this).data('arc_section').show(); });
							jQuery('h3.hsection').each(function(){ jQuery(this).data('arc_section').show(); });
							jQuery('h4.hsection').each(function(){ jQuery(this).data('arc_section').show(); });
							jQuery('#sect_status').each(function(){ jQuery(this).data('arc_section').show(); });
						//jQuery('h4.hsection').each(function(){ jQuery(this).data('arc_section').show(); });
						//jQuery('#sect_p_collection').each(function(){ jQuery(this).data('arc_section').show();  });
						//jQuery('#sect_p_info').each(function(){ jQuery(this).data('arc_section').show();  });
						//jQuery('#sect_info').each(function(){ jQuery(this).data('arc_section').hide();   });
						toggle_status_open = true;
					}
			}

function (e) {
					e.preventDefault();
					e.stopImmediatePropagation();
					if (specialized_fields_closed){
						jQuery('.specialized_sect').show();
						var button_label = jQuery('#top_sect button').text().replace('+', '-');
						jQuery('#top_sect button').text(button_label)
						specialized_fields_closed = false;
					} else {
						jQuery('.specialized_sect').hide();
						var button_label = jQuery('#top_sect button').text().replace('-', '+');
						jQuery('#top_sect button').text(button_label)
						specialized_fields_closed = true;
					}
				}

function (e) {
					e.preventDefault();
					e.stopImmediatePropagation();
					var h = jQuery(this).closest('.hsection').data('arc_section').show();
					fc.createFieldModel('ea:publication:statement',null,null);
					fc._renderForm();
			   }

opts.display_handler:
function (item_id, itemFields) {

		//console.log("@@DISPLAY-HANDLER",item_id);

		var my_root = jQuery('<div class="subitem">');
		//	console.log(itemFields.ndata);
//		jQuery.each(itemFields.ndata,function(idx,item){
//			console.log("display-item: ", item);
//		});

		var v_title = '???';
		var v_title_model = itemFields.getFirstFieldModel('dc:title:');
		if (v_title_model != null){
			v_title = v_title_model.value();
		}
		var li;

		var link = jQuery('<a>');
		link.attr('href','/archive/item/' + item_id);
		link.attr('target','_blank');
		var h = jQuery('<h4>');
		h.text(v_title);
		h.appendTo(link);
		link.appendTo(my_root);


		var display_field = function(key,label){
			var li;
			var fs = itemFields.getFieldModels(key);
			if (fs.length > 0){
				for (i in fs) {
					li = jQuery('<li>');
					jQuery('<span class="dlab">').text(label).appendTo(li);
					var an = fs[i].value();
					jQuery('<span class="dval">').text(an).appendTo(li);
					li.appendTo(ul);
				}
			}
		};

		var ul = jQuery('<ul class="subitem">');

		 li = jQuery('<li>');
		 jQuery('<span class="dlab">').text(title_label).appendTo(li);
		 jQuery('<span class="dval">').text(v_title).appendTo(li);
		 li.appendTo(ul);

		display_field('ea:title:uniform',tile_uniform_label);
		display_field('dc:contributor:author','author:');
		display_field('ea:status:','status:');
		ul.appendTo(my_root);


		var parent_model = this.parent_model;
		var parent_id = parent_model.id();
		var cmd = {
			action : 'setupField',
			opts : {
				'type' : 'text',
				'show_help' : false,
				'key' : 'ea:title:specific',
				'label' : 'title specific',
				'link_id': parent_id,
				}
			};
		this.fc.execCMD(cmd,my_root);
		return my_root;
	}

