					options.default_selection = window.form_object_type_default,
					options.select_values = window.form_object_types;
//					options.read_only = (! form_new_record_flag);
					return options;
				}

opts.fn:
function (context) {
				/** @type FormControler */
				var fc = context.fc;
				var subjects = fc.getFieldModels('ea:subj:');
				jQuery.each(subjects,function(i,subj){
					var sid = subj.id();
					var childs = fc.getFieldModels(null, sid);
					jQuery.each(childs,function(i,c){
						if (c.key().match('^ea:subj:') && ! c.refItem()){
							var cid = c.id();
							var cl = fc.getFieldModels(null, cid).length;
							if (cl == 0){
								c.value(null);
							}
						}
					});

				});
			}

